(()=>{function e(){document.documentElement.className.includes("dark")?(document.documentElement.classList.remove("dark"),localStorage.setItem("theme","light")):(document.documentElement.classList.add("dark"),localStorage.setItem("theme","dark"))}window.addEventListener("DOMContentLoaded",t=>{const n=document.getElementById("theme-switcher");n&&n.addEventListener("click",()=>{e()}),window.matchMedia("(prefers-color-scheme: dark)").addEventListener("change",t=>{t.matches&&localStorage.getItem("theme")==="light"&&e()})})})()